	public static String[][]randomStars(Player player, String[][] arr) {

		int strCount = arr.length;
		int elementCount = arr[0].length;

		int countStars = 10 * player.getCurrentLevel();
		int randomNumber;

		for (int i = 0; i < strCount; i++) {
			for (int j = 0; j < elementCount; j++) {
				randomNumber = (int) (Math.random() * 10);
				if (arr[i][j].equals("  ") && randomNumber == 7 && countStars > 0) {
					arr[i][j] = "‚òÜ ";
					countStars -= 1;
					i++;
				}
				if (countStars == 0) {
					return arr;
				}
			}
		}

		return arr;
	}
